// page init
 $(window).on('load', function() {
	initTouchNav();
	initCarousel();
	initSlideShow();
	initDropDownClasses();
	initMobileNav();
	initParallaxBg();
	initIsotopeFilter();
	initAddOpener();
	//initHeaderClone();
	//initHeaderFixed();
	initSearchForm();
	initTabjumb();
	jQuery('input, textarea').placeholder();
});

// scroll gallery init
function initCarousel() {
	jQuery('.blog-slider').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		autoRotation: true,
		maskAutoSize: false,
		pauseOnHover:false,
		vertical: true,
		switchTime: 3000,
		animSpeed: 500
	});
	jQuery('.main-carousel').scrollGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		pauseOnHover:false,
		autoRotation: true,
		switchTime: 5000,
		animSpeed: 1500,
		step:1
	});
	jQuery('.products-slider').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.col',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		pauseOnHover:false,
		autoRotation: false,
		switchTime: 5000,
		animSpeed: 1500,
		circularRotation: false,
		step:1
	});
	jQuery('.carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		pauseOnHover:false,
		autoRotation: true,
		switchTime: 5000,
		stretchSlideToMask: true,
		animSpeed: 1500
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		pauseOnHover: false,
		autoHeight: true,
		event: 'click',
		autoRotation: true,
		generatePagination: '.pagination',
		switchTime: 6000,
		animSpeed: 3000
	});
}

// mobile menu init
function initMobileNav() {
	jQuery('html').mobileNav({
		menuActiveClass: 'nav-open',
		menuOpener: '.navbar-toggler'
	});
	jQuery('#nav li').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'hover',
		menuOpener: '>.opener',
		menuDrop: '>ul'
	});

	jQuery('.tab-content .tab-pane').mobileNav({
		hideOnClickOutside: true,
		menuActiveClass: 'open',
		menuOpener: '.opener',
		menuDrop: '.data'
	});
}

// handle dropdowns on mobile devices
function initTouchNav() {
	 if($(window).width() > 767){
		jQuery('#nav').each(function(){
			new TouchNav({
				navBlock: this
			});
		});
	}
	jQuery('.tabs').each(function(){
		new TouchNav({
			navBlock: this,
			menuItems: '.nav-pills',
		});
	});
}
